document.addEventListener('DOMContentLoaded',function(){const e=[{titulo:'Água Limpa para Todos',descricao:'Instalação de filtros e sistemas de captação de água em comunidades rurais com escassez.',ano:2024,status:'Ativo',imagemUrl:'img/img-agua.jpg'},{titulo:'Alfabetização Digital',descricao:'Cursos de informática e acesso à internet para idosos e jovens de baixa renda.',ano:2023,status:'Concluído',imagemUrl:'img/img-digital.jpg'},{titulo:'Horta Comunitária',descricao:'Criação de hortas orgânicas em espaços urbanos para gerar renda e alimentação saudável.',ano:2024,status:'Em Andamento',imagemUrl:'img/img-horta.jpg'}],t=document.getElementById('lista-projetos');if(!t)return console.error("Erro: O elemento com ID 'lista-projetos' não foi encontrado na página."),void 0;function o(t){return`\n            <article class="projeto-card">\n                <img src="${t.imagemUrl}" alt="Imagem do projeto ${t.titulo}">\n                <h3>${t.titulo} (${t.ano})</h3>\n                <p>${t.descricao}</p>\n                <span class="status ${t.status.toLowerCase().replace(' ','-')}">Status: ${t.status}</span>\n            </article>\n        `}let s='';e.forEach(e=>{s+=o(e)}),t.innerHTML=s,console.log('Projetos carregados dinamicamente com sucesso!')});